import React, { Component } from 'react'
import Select from 'react-select'
import 'bootstrap/dist/css/bootstrap.min.css';

export class MainPage extends Component {
    constructor(props){
        super(props);

        this.state = { data: [], appName:'', bundleId:'', storeName:'', server:'', trigger: 'Active'}
               

        // this.handleSubmit = this.handleSubmit.bind(this);
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
    }

    async categoryChange(event) {    
      console.log('CATEGORY SUBMISSION 1',event.label);
      console.log('CATEGORY SUBMISSION 2',event.value);
      console.log('CATEGORY SUBMISSION 3',event.bundleId);
      console.log('CATEGORY SUBMISSION 4',event.storeName);
      console.log('CATEGORY SUBMISSION 5',event.firstAPI);
      this.setState({
        appName: event.label,
        bundleId: event.bundleId,
        storeName: event.storeName,
        server: event.value
      })
      
      // console.log('BUNDLES',this.bundlesdata.bundles);
      // this.setState({firstAPI: event.firstAPI}); 
      
      await fetch(event.firstAPI)
      .then((response) => response.json())
      .then( (actualData) => {
        console.log('response: ', actualData);
        this.setState({data: actualData})
        console.log('dataaaaa', this.state);
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }
    

    activeStatus(event){
      console.log(this.state.trigger);
      if(this.state.trigger === 'NotActive'){
        this.setState({trigger: 'Active'},()=>{
          console.log('Active status IF', this.state.trigger);

        })
      }
      
    }

    // async handleSubmit(event){
    //   event.preventDefault();
    //   await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${this.state.bundle}`)
    //     .then((response) => response.json())
    //     .then( (actualData) => {
    //       console.log('response: ', actualData);
    //       if(actualData.status)
    //         this.setState({data: actualData.frames})
    //       else
    //         console.log("ERROR");
    //     })
    //     .catch((err) => {
    //      console.log(err.message);
    //     });
    // }

  render() {
    const {trigger, setTrigger, pic, setPic} = this.props
    return (
        <>
      <h1>APPS</h1>
      <form className='form-box'>
        <Select placeholder='Select App Name...' name='category' required id='category' className='' onChange={this.categoryChange} options={this.props.categoryOptions}/>
        <br/>
        
        <input type="submit" value="SHOW" onClick={this.activeStatus}/>
      </form>

      
      {
      this.state.trigger === 'Active' &&
        <>
          {this.state.appName}
          <br/>
          {this.state.storeName}
          <br/>
          {this.state.bundleId}
          <br/>
          {this.state.server}
          <br/>
          {this.state.trigger}
          <br/>

        </>
      }


      <br/>
      

      {/* <DisplayFrame trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic}/> */}
      </>
    )
  }
}

export default MainPage
