import React, { Component } from 'react'
import Select from 'react-select'

export class NameForm extends Component {
    constructor(props) {
        super(props);
        this.state = {height: '', width: '', X: '', Y: '', category: 'love', bundle: '', image: [], images: {}};
        this.handleChangeX = this.handleChangeX.bind(this);
        this.handleChangeY = this.handleChangeY.bind(this);
        this.handleChangeH = this.handleChangeH.bind(this);
        this.handleChangeW = this.handleChangeW.bind(this);
        this.categoryChange = this.categoryChange.bind(this);
        this.onImageChange = this.onImageChange.bind(this);
        this.onImagesChange = this.onImagesChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChangeX(event) {    this.setState({X: event.target.value}); console.log("X: ", event.target.value); }
      handleChangeY(event) {    this.setState({Y: event.target.value}); console.log("Y: ", event.target.value); }
      handleChangeH(event) {    this.setState({height: event.target.value}); console.log("H: ", event.target.value); }
      handleChangeW(event) {    this.setState({width: event.target.value}); console.log("W: ", event.target.value); }
      categoryChange(event) {    this.setState({category: event.target.value});  }
      onImageChange(event) {
        console.log(event.target.files);
        this.setState({image: event.target.files[0]});  
      }
      onImagesChange(event) {
        console.log('multiple',typeof(event.target.files));
        console.log('multiple',event.target.files[1]);
        // var filesArr = [];
        // for(let a=0; a<event.target.files.length; a++){
        //   filesArr.push(event.target.files[a])
        // }
        // console.log("FILES",filesArr);
        this.setState({images: event.target.files});  
        console.log(this.state);
      }
      async handleSubmit(event) {
        
        event.preventDefault();
        const formData = new FormData();
        for (let x in this.state) {
          formData.append(x, this.state[x]);          //saving data in formData
          console.log(x, ': ', this.state[x]);
          
       }
       var api;
       if(this.state.category === 'love'){
         api = "http://172.16.0.107:9001/api/rooms/love";
         console.log("API 1 CALLED");
        }
        else if(this.state.category === 'comic'){
          api = "http://172.16.0.107:9001/api/rooms/comic"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'birthday'){
          api = "http://172.16.0.107:9001/api/rooms/birthday"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'magazine'){
          api = "http://172.16.0.107:9001/api/rooms/magazine"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'news'){
          api = "http://172.16.0.107:9001/api/rooms/news"
          console.log("API 2 CALLED");
       }
       else{
         console.log("-----------------");
       }

      //  wait(1)
       if(api){
        const data = await fetch(api, {
          method: "post",
          // headers: { "Content-Type": "multipart/form-data" },
          body: formData,
        });
        const uploadedImage = await data.json();
        console.log(uploadedImage);
        alert("Frame is saved");
       }


      }
    
      render() {
        return (
          <>

          {/* <div className="sidenav">
            <a href="#about">About</a>
            <a href="#services">Services</a>
            <a href="#clients">Clients</a>
            <a href="#contact">Contact</a>
          </div> */}

          <div className='main'>
          <h2>Add a Frame</h2>
          <form onSubmit={this.handleSubmit}>
            <Select name='selectList' required id='selectList' onChange={this.categoryChange} options={this.props.categoryOptions}/>
            <br/>
            <Select name='selectBundle' required id='selectBundle' onChange={this.bundleChange} options={this.props.categoryOptions}/>
            <br/>
                <br/>
                <br/>
                Coordinates
                <br/>
                X: 
                <input type="text" value={this.state.X} onChange={this.handleChangeX} />
                <br/>
                Y: 
                <input type="text" value={this.state.Y} onChange={this.handleChangeY} />
                <br/>
                <br/>
                H: 
                <input type="text" value={this.state.height} onChange={this.handleChangeH} />
                <br/>
                W: 
                <input type="text" value={this.state.width} onChange={this.handleChangeW} />
                <br/>
                <br/>

                <input type="file" required onChange={this.onImageChange} />
                {/* <input type="file" multiple accept='image/*' onChange={this.onImagesChange} /> */}
                <br/>
              <input type="submit" value="Submit" />
          </form>
          </div>
          </>
        );
      }
}

export default NameForm
