import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


export class UploadLoveFrames extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: 'Love Frames', value: '5ff5a1d93bbf9374326985de,love'},
          {label: 'Comic Frames', value: '5ff7fab5f0dfd4c8479c5912,comicsframes'},
          {label: 'Birthday Frames', value: '5ff5a1d93bbf9374326985db,birthdayframes'},
          {label: 'Magazine Frames', value: '5ff7fab5f0dfd4c8479c5913,magzineframes'},
          {label: 'News Frames', value: '5ff7fab5f0dfd4c8479c5914,newsframes'}
        ]
        
        this.state = {
          data: [] ,
          category: '',
          folder: '',
          bundle: '',
          bundles: [],
          trigger: false,
          trigger2: false,
          X: '',
          Y: '',
          H:'',
          W:'',
          // image:[],
          images: []
        }
        
        this.categoryChange = this.categoryChange.bind(this);
        this.bundleChange = this.bundleChange.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
        this.handleX = this.handleX.bind(this)
        this.handleY = this.handleY.bind(this)
        this.handleH = this.handleH.bind(this)
        this.handleW = this.handleW.bind(this)
        this.handleImage = this.handleImage.bind(this)
        this.handleImages = this.handleImages.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async categoryChange(event) {
        
      const arr = event.target.value.split(',')
      console.log('changing', event.target.value);
      this.setState({category: arr[0], folder: arr[1], active: 'NotActive', trigger: false, trigger2: false}); 
      
      
      await fetch(`http://161.97.164.28:8080/api/bundles/retrieveBundles?category=${arr[0]}`)
      .then((response) => response.json())
      .then( (actualData) => {
        if(actualData.status)
        {
          this.setState({data: actualData.frames, bundles: actualData.data, trigger: true})
        }
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }
    async bundleChange(event){
      const bName = event.target.value.toLowerCase()
      this.setState({bundle: bName, active: 'NotActive',X:'',Y:'',H:'',W:''})

      await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${bName}`)
        .then((response) => response.json())
        .then( (actualData) => {
          console.log('response: ', actualData);
          if(actualData.status){
            this.setState({data: actualData.frames, trigger2: true})
            console.log('response:', this.state.data);
          }
          else
            console.log("ERROR");
        })
        .catch((err) => {
         console.log(err.message);
        });

    }
    
    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

    handleX=(event)=>{console.log(event.target.value); this.setState({X: event.target.value})}
    handleY=(event)=>{console.log(event.target.value); this.setState({Y: event.target.value})}
    handleH=(event)=>{console.log(event.target.value); this.setState({H: event.target.value})}
    handleW=(event)=>{console.log(event.target.value); this.setState({W: event.target.value})}
    handleImage=(event)=>{
      console.log(typeof(event.target.files[0]));
      this.setState({image: event.target.files[0]}); 
      console.log('=================>>>>>>',this.state);
    }
    handleImages = (event)=>{
      console.log('-------------',event.target.files.length);
      // console.log('multiple',typeof(event.target.files));
        // console.log('multiple',event.target.files[1]);
        var filesArr = [];
        for(var i in event.target.files)
          if(i< event.target.files.length)
            filesArr.push(event.target.files[i])
        // for(let a=0; a<event.target.files.length; a++){
        //   filesArr.push(event.target.files[a])
        // }
        // console.log("FILES",filesArr);
        // this.setState({images: event.target.files});  
        this.setState({images: filesArr}); 
        console.log('=================>>>>>>',this.state);
    }
    
    handleSubmit = async (event)=>{
      event.preventDefault();
      const formData = new FormData();
      for (let x in this.state) {
        formData.append(x, this.state[x]);          //saving data in formData
        console.log(x, ': ', this.state[x]);
        
      }
      // console.log('-------------->>>>>>', this.formData);
       var api= "http://172.16.0.107:9001/api/rooms/sendImage";
       
      //  wait(1)
       if(api){
        const data = await fetch(api, {
          method: "post",
          // headers: { "Content-Type": "multipart/form-data" },
          body: formData,
        });
        const uploadedImage = await data.json();
        console.log(uploadedImage);
        alert("Frame is saved");
       }
    }

  render() {
    return (
        <>
            <h2>Love Frames App</h2>
            <form className='form-box' onSubmit={this.handleSubmit}>
                <select onChange={this.categoryChange}>
                  <option>Select Option</option>

                  {this.categories.map(this.MakeItem)}
                </select>        
                {
                  this.state.trigger === true &&
                  <>
                    <br/>
                      <select onChange={this.bundleChange}>
                        <option>Select Option</option>
                    
                        {this.state.bundles.map(this.MakeItem)}
                      </select>          
                  </>
                }
                {
                    this.state.trigger2 === true &&

                    <>
                        <br/>
                        <br/>
                        Coordinates
                        <br/>
                        X:&nbsp;
                        <input type="text1" value={this.state.X} onChange={this.handleX}/>
                        &nbsp;&nbsp;&nbsp;
                        Y:&nbsp;
                        <input type="text2" value={this.state.Y} onChange={this.handleY}/>
                        <br/>
                        <br/>
                        Position
                        <br/>
                        H:&nbsp;
                        <input type="text3" value={this.state.H} onChange={this.handleH}/>
                        &nbsp;&nbsp;
                        W:&nbsp;
                        <input type="text4" value={this.state.W} onChange={this.handleW}/>
                        <br/>
                        <br/>
                        <b>Upload Frame: </b>
                        <input type="file" required onChange={this.handleImage}/>
                        <br/>
                        
                        <input type="submit" value="Upload Data" onClick={()=>{}}/>
                    </>
                }
            </form>  
        </>
    )
  }
}

export default UploadLoveFrames
