import { wait } from '@testing-library/user-event/dist/utils';
import React, { Component } from 'react'

export class NameForm extends Component {
    constructor(props) {
        super(props);
        this.state = {H: 0, W: 0, X: 0, Y: 0, category: 'love', image: []};
        this.handleChangeX = this.handleChangeX.bind(this);
        this.handleChangeY = this.handleChangeY.bind(this);
        this.handleChangeH = this.handleChangeH.bind(this);
        this.handleChangeW = this.handleChangeW.bind(this);
        this.categoryChange = this.categoryChange.bind(this);
        this.onImageChange = this.onImageChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChangeX(event) {    this.setState({X: event.target.value}); console.log("X: ", event.target.value); }
      handleChangeY(event) {    this.setState({Y: event.target.value}); console.log("Y: ", event.target.value); }
      handleChangeH(event) {    this.setState({H: event.target.value}); console.log("H: ", event.target.value); }
      handleChangeW(event) {    this.setState({W: event.target.value}); console.log("W: ", event.target.value); }
      categoryChange(event) {    this.setState({category: event.target.value});  }
      onImageChange(event) {
        console.log(event.target.files.length);
        var filesArr = [];
        for(a=0; a<event.target.files.length; a++){
          filesArr.append(event.target.files[a])
        }
        console.log("FILES",filesArr);
        this.setState({image: event.target.files});  
      }
      async handleSubmit(event) {
        
        const formData = new FormData();
        for (let x in this.state) {
          formData.append(x, this.state[x]);          //saving data in formData
          console.log(x, ': ', this.state[x]);
          
       }
       var api;
       if(this.state.category === 'love'){
         api = "http://172.16.0.107:9001/api/rooms/checkImage1";
         console.log("API 1 CALLED");
        }else if(this.state.category === 'comic'){
          api = "http://172.16.0.107:9001/api/rooms/checkImage2"
          console.log("API 2 CALLED");
       }else{
         console.log("-----------------");
       }

      //  wait(1)
      //  const data = await fetch(api, {
      //      method: "post",
      //      // headers: { "Content-Type": "multipart/form-data" },
      //      body: formData,
      //    });
      //    const uploadedImage = await data.json();
      //    console.log(uploadedImage);

      //   event.preventDefault();
      }
    
      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            
              <select name="selectList" id="selectList" onChange={this.categoryChange}>
                 <option value="love">Love Frames</option>
                 <option value="comic">Comic Frames</option>
                 <option value="birthday">Birthday Frames</option>
                 <option value="magazine">Magazine Frames</option>
                 <option value="news">News Frames</option>
              </select>
              <br/>
              <br/>
              X: 
              <input type="text" value={this.state.X} onChange={this.handleChangeX} />
              <br/>
              Y: 
              <input type="text" value={this.state.Y} onChange={this.handleChangeY} />
              <br/>
              <br/>
              H: 
              <input type="text" value={this.state.H} onChange={this.handleChangeH} />
              <br/>
              W: 
              <input type="text" value={this.state.W} onChange={this.handleChangeW} />
              <br/>
              <br/>
              
              <input type="file" required multiple onChange={this.onImageChange} />
              <br/>
            <input type="submit" value="Submit" />
          </form>
        );
      }
}

export default NameForm
