import './App.css';
import React, { useEffect, useState } from 'react';
import NameForm from './component/NameForm';
import DisplayPanel from './component/DisplayPanel';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './component/Header'


function App() {
  const [trigger, setTrigger]=useState(false)
  const [triggerDisplay, setTriggerDisplay]=useState(true)
  const [triggerAdd, setTriggerAdd]=useState(false)
  const [pic, setPic]=useState("")
  const categoryOptions = [
    {label: 'Love Frames', value: '5ff5a1d93bbf9374326985de'},
    {label: 'Comic Frames', value: '5ff7fab5f0dfd4c8479c5912'},
    {label: 'Birthday Frames', value: '5ff5a1d93bbf9374326985db'},
    {label: 'Magazine Frames', value: '5ff7fab5f0dfd4c8479c5913'},
    {label: 'News Frames', value: '5ff7fab5f0dfd4c8479c5914'},          
  ];
  const bundleOptions = [
    {label: 'Bundle 1', value: 'bundle1'},
    {label: 'Bundle 2', value: 'bundle2'},
    {label: 'Bundle 3', value: 'bundle3'},
    {label: 'Bundle 4', value: 'bundle4'},
    {label: 'Bundle 5', value: 'bundle5'},
    {label: 'Bundle 6', value: 'bundle6'},
    {label: 'Bundle 7', value: 'bundle7'},
    {label: 'Bundle 8', value: 'bundle8'},
    {label: 'Bundle 9', value: 'bundle9'},
    {label: 'Bundle 10', value: 'bundle10'},
    {label: 'Bundle 11', value: 'bundle11'},
  ];


  useEffect(()=>{
  },[])

  return (
    <div className="App">
      <Header setTriggerDisplay={setTriggerDisplay} setTriggerAdd={setTriggerAdd}/>

      {
        triggerDisplay === true &&
        <DisplayPanel trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic} categoryOptions={categoryOptions}/>
      }
      {
        triggerAdd === true &&
        <NameForm/>

      }


    </div>
  );
}

export default App;
