import React, { Component } from 'react'
import Select from 'react-select'
import 'bootstrap/dist/css/bootstrap.min.css';

export class SmartyMenJacket extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "beard",value: "beard"        },
          {label: "cap",value: "cap"            },
          {label: "chest",value: "chest"        },
          {label: "eightpack",value: "eightpack"},
          {label: "eyeball",value: "eyeball"    },
          {label: "eyebrowes",value: "eyebrowes"},
          {label: "filters",value: "filters"    },
          {label: "glasses",value: "glasses"    },
          {label: "hair",value: "hair"          },
          {label: "jackets",value: "jackets"    },
          {label: "mustach",value: "mustach"    },
          {label: "scarf",value: "scarf"        },
          {label: "sixpack",value: "sixpack"    },
          {label: "tattoo",value: "tattoo"      },
          {label: "tie",value: "tie"            }
          ];
        this.count = 0        

        this.loadCategories = this.loadCategories.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.bundleChange = this.bundleChange.bind(this);
    }

     loadCategories(){
      fetch('http://164.68.111.220:9001/api/tas/smartymentags')
      .then((response) => response.json())
      .then( (actualData) => {
        console.log('response: ', actualData);
        if(actualData)
        {
          this.categories = actualData;
        }
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      });
      this.count=1;
      // console.log('load categories');
    }

    async categoryChange(event) {
      this.setState({category: event.value, active: 'NotActive'}); 
      this.bundlesdata.trigger=false;
      
      await fetch(`http://161.97.164.28:8080/api/bundles/retrieveBundles?folder=${event.value2}`)
      .then((response) => response.json())
      .then( (actualData) => {
        console.log('response: ', actualData);
        this.bundlesdata.bundles = actualData.data ;
        this.bundlesdata.trigger = true ;
        console.log('BUNDLES',this.bundlesdata.bundles);
        if(actualData.status)
        {
          this.setState({data: actualData.frames})
        }
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }
    async bundleChange(event){
      console.log('BUNDLE SUBMISSION',event.value);
      this.setState({bundle: event.value.toLowerCase(), active: 'NotActive'})

      await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${event.value.toLowerCase()}`)
        .then((response) => response.json())
        .then( (actualData) => {
          console.log('response: ', actualData);
          if(actualData.status)
            this.setState({data: actualData.frames})
          else
            console.log("ERROR");
        })
        .catch((err) => {
         console.log(err.message);
        });

    }

    activeStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'NotActive'){
        this.setState({active: 'Active'},()=>{
          console.log('Active status IF', this.state.active);

        })
      }
      
    }

    async handleSubmit(event){
      event.preventDefault();
      // await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${this.state.bundle.toLowerCase()}`)
      //   .then((response) => response.json())
      //   .then( (actualData) => {
      //     console.log('response: ', actualData);
      //     if(actualData.status)
      //       this.setState({data: actualData.frames})
      //     else
      //       console.log("ERROR");
      //   })
      //   .catch((err) => {
      //    console.log(err.message);
      //   });
    }

  render() {
    const {trigger, setTrigger, pic, setPic} = this.props
    return (
        <>
      <h1>Smarty Men Jacket</h1>
      {
        this.count ==0 &&
        this.loadCategories()
      }
        <Select placeholder='Select Frame...' name='category' required id='category' className=''  options={this.categories}/>
        
        {/* <input type="submit" value="SHOW" onClick={this.activeStatus}/> */}
    

      <br/>
      {/* {
        (this.state.active === "Active") &&
        this.state.data.map(result=>(
          <>
            <img className='imgShow' key={result.image} src={result.image} alt="Frames" onClick={()=>{setTrigger(true); setPic(result.image)}}></img>
          </>
        ))
      } */}

      {/* <DisplayFrame trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic}/> */}
      </>
    )
  }
}

export default SmartyMenJacket
