import React, { Component } from 'react'
import AddFrame from './AddFrame';
import AddBundle from './AddBundle';


export class NameForm extends Component {
    constructor(props){
      super(props);
      this.state  = {frame: true, bundle: false, category: false};
      this.handleFrame= this.handleFrame.bind(this);
      this.handleBundle= this.handleBundle.bind(this);
      this.handleCategory= this.handleCategory.bind(this);
    }

    handleFrame(event){
      this.setState({frame: true, bundle: false, category: false})
    }
    handleBundle(event){
      this.setState({frame: false, bundle: true, category: false})
    }
    handleCategory(event){
      this.setState({frame: false, bundle: false, category: true})
    }


      render(props) {
        return (
          <>

          <div className="sidenav">
            <li onClick={()=>{this.handleFrame()}}>Add Frame</li>
            <li onClick={()=>{this.handleBundle()}}>Add Bundle</li>
            <li onClick={()=>{this.handleCategory()}}>Add Category</li>
          </div>

          <div className='main'>
            {
              this.state.frame === true &&
              <AddFrame
                categoryOptions={this.props.categoryOptions}
                bundleOptions={this.props.bundleOptions}
              />
            }
            {
              this.state.bundle === true &&
              <AddBundle
                categoryOptions={this.props.categoryOptions}
                bundleOptions={this.props.bundleOptions}
              />
            }
            {
              this.state.category === true &&
              <AddFrame
                categoryOptions={this.props.categoryOptions}
                bundleOptions={this.props.bundleOptions}
              />
            }

          </div>
          </>
        );
      }
}

export default NameForm
