import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { ThreeSixtyRounded } from '@material-ui/icons';


export class UploadSmartyMen extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "beard",    value: "beard"    },
          {label: "cap",      value: "cap"      },
          {label: "chest",    value: "chest"    },
          {label: "eightpack",value: "eightpack"},
          {label: "eyeball",  value: "eyeball"  },
          {label: "eyebrowes",value: "eyebrowes"},
          {label: "filters",  value: "filters"  },
          {label: "glasses",  value: "glasses"  },
          {label: "hair",     value: "hair"     },
          {label: "jackets",  value: "jackets"  },
          {label: "mustach",  value: "mustach"  },
          {label: "scarf",    value: "scarf"    },
          {label: "sixpack",  value: "sixpack"  },
          {label: "tattoo",   value: "tattoo"   },
          {label: "tie",      value: "tie"      }
        ]
        
        this.state = {
          data: [] ,
          category: '',
          trigger: false,
          image:[],
          images: ''
        }
        
        this.categoryChange = this.categoryChange.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
        this.handleImage = this.handleImage.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefiles = this.handlefiles.bind(this);
    }

    handlefiles(event){
      console.log('FILES..............',event.target.files);
      this.setState({images: event.target.files})
    }

    async categoryChange(event) {
      this.setState({category: event.target.value ,trigger: true, active: 'NotActive'})
      console.log('TRIGGER',this.state);
    }
    
    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

    handleImage=(event)=>{      
      this.setState({image: event.target.files[0]}); 
    }
    handleSubmit = async (event)=>{
      event.preventDefault();
      const formData = new FormData();
      for (let x in this.state) {
        formData.append(x, this.state[x]);          //saving data in formData
        console.log(x, ': ', this.state[x]);
        
      }
      // console.log('-------------->>>>>>', this.formData);
       var api= "http://172.16.0.107:9001/api/rooms/sendImages";
       
      //  wait(1)
       if(api){
        const data = await fetch(api, {
          method: "post",
          // headers: { "Content-Type": "multipart/form-data" },
          body: formData,
        });
        const uploadedImage = await data.json();
        console.log(uploadedImage);
        alert("Frame is saved");
       }
    }

  render() {
    return (
        <>
            <h2>Smarty Men App</h2>
            <form className='form-box' onSubmit={this.handleSubmit}>
                <select onChange={this.categoryChange}>
                  <option>Select Option</option>

                  {this.categories.map(this.MakeItem)}
                </select>        
                
                {
                    // this.state.trigger === true &&

                    <>
                        <br/>
                        <br/>
                        {/* <b>Upload Image : </b> */}
                        {/* <input type="file" required onChange={this.handleImage}/> */}
                        <b>Upload Images : </b>
                        <input type="file" multiple required onChange={this.handlefiles}/>
                        <br/>    
                        <br/>    
                        <input type="submit" value="Upload Data" onClick={()=>{}}/>
                        
                    </>
                }

            </form>  
        </>
    )
  }
}

export default UploadSmartyMen
