import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

export class NaturePhotosApp extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "All",      value: "All"           },
          {label: "Desert",   value: "Desert"        },
          {label: "Forests",  value: "Forests"       },
          {label: "Grass",    value: "Grass"         },
          {label: "Mountain", value: "Mountain"      },
          {label: "Ocean",    value: "Ocean"         }
        ]
        this.subCategories = [
          {label: "background", value: "background"  },
          {label: "overlay",    value: "overlay"     },
          {label: "thumbnails", value: "thumbnails"  }
        ]
        this.state = {category:'',trigger: false, data:[], active: 'NotActive', subcategories: []};
                
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.bundleChange = this.bundleChange.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
    }


    async categoryChange(event) {
      var newdata = [
        {label: "background", value: "background"  },
        {label: "overlay",    value: "overlay"     },
        {label: "thumbnails", value: "thumbnails"  }
      ]

      this.setState({category: event.target.value ,trigger: true, active: 'NotActive'})
      console.log('TRIGGER',this.state);
    }
    async bundleChange(event){
      console.log(this.state)
      this.setState({active: 'NotActive'})
      await fetch(`http://161.97.164.28:8080/api/assets/getNatureAssets?folder=${this.state.category}&tag=${event.target.value}`)
      .then((response) => response.json())
      .then( (actualData) => {
        console.log('response: ', actualData);
        if(actualData){
            this.setState({data: actualData})
          }
          else
            console.log("ERROR");
        })
        .catch((err) => {
         console.log(err.message);
        });
    }

    activeStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'NotActive'){
        this.setState({active: 'Active'},()=>{
          console.log('Active status IF', this.state.active);
          console.log('Active status IF', this.state);
        })
      }
    }

    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

  render() {
    const {setTrigger, setPic} = this.props
    return (
        <>
      <h1>Nature Photo Editor</h1>
      <div className='form-box'>
        <select onChange={this.categoryChange}>
          <option>Select Option</option>
          
          {this.categories.map(this.MakeItem)}
        </select>
        {
          this.state.trigger === true &&
          <select onChange={this.bundleChange}>
            <option>Select Option</option>

            {this.subCategories.map(this.MakeItem)}
          </select>      
        }
        <input type="submit" value="SHOW" onClick={this.activeStatus}/>
        <br/>
      </div>
        

      <br/>
      {
        (this.state.active === "Active") &&
        this.state.data.map(result=>(
          <>
            <img className='imgShow' key={result.image} src={result.image} alt="Frames" onClick={()=>{setTrigger(true); setPic(result.image)}}></img>
          </>
        ))
      }
      </>
    )
  }
}

export default NaturePhotosApp
