import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './component/Header'


function App() {
  const [trigger, setTrigger]=useState(false)
  const [triggerDisplay, setTriggerDisplay]=useState(true)
  const [triggerAdd, setTriggerAdd]=useState(false)
  const [pic, setPic]=useState("")


  useEffect(()=>{
  },[])

  return (
    <div className="App">
      <Header setTriggerDisplay={setTriggerDisplay} setTriggerAdd={setTriggerAdd}/>

      <div className="w3-sidebar w3-light-grey w3-bar-block" style="width:25%">
        {/* <h3 className="w3-bar-item">Menu</h3> */}
        <a >Link 1</a>
        <a >Link 2</a>
        <a >Link 3</a>
      </div>
      {
        triggerDisplay === true &&
        <DisplayPanel trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic}/>
      }
      {
        triggerAdd === true &&
        <NameForm/>

      }


    </div>
  );
}

export default App;
