import React, { Component } from 'react'
import Select from 'react-select'
import 'bootstrap/dist/css/bootstrap.min.css';

export class SmartyMenJacket extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "beard",value: "beard"        },
          {label: "cap",value: "cap"            },
          {label: "chest",value: "chest"        },
          {label: "eightpack",value: "eightpack"},
          {label: "eyeball",value: "eyeball"    },
          {label: "eyebrowes",value: "eyebrowes"},
          {label: "filters",value: "filters"    },
          {label: "glasses",value: "glasses"    },
          {label: "hair",value: "hair"          },
          {label: "jackets",value: "jackets"    },
          {label: "mustach",value: "mustach"    },
          {label: "scarf",value: "scarf"        },
          {label: "sixpack",value: "sixpack"    },
          {label: "tattoo",value: "tattoo"      },
          {label: "tie",value: "tie"            }
          ];  
        this.data = [];   
        this.active = 'NotActive'
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
    }

     

    async categoryChange(event) {
      this.active= 'NotActive'; 
      
      await fetch(`http://164.68.111.220:9001/api/tas/assetsByTag?tag=${event.value}`)
      .then((response) => response.json())
      .then( (actualData) => {
        console.log('response: ', actualData);
        
        if(actualData)
        {
          // this.setState({data: actualData.frames})
        }
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }

    activeStatus(event){
      console.log(this.active);
      if(this.active === 'NotActive'){
        this.active= 'Active'
        console.log(this.active);
      }
      
    }

  render() {
    const {trigger, setTrigger, pic, setPic} = this.props
    return (
        <>
      <h1>Smarty Men Jacket</h1>
      <div className='form-box'>
        <Select placeholder='Select Frame...' name='category' required id='category' className='' onChange={this.categoryChange} options={this.categories}/>
        
        <input type="submit" value="SHOW" onClick={this.activeStatus}/>
      </div>

      <br/>
      {
        (this.active === "Active") &&
        console.log(this.active)
        // this.data.map(result=>(
        //   <>
        //     <img className='imgShow' key={result.image} src={result.image} alt="Frames" onClick={()=>{setTrigger(true); setPic(result.image)}}></img>
        //   </>
        // ))
      }

      {/* <DisplayFrame trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic}/> */}
      </>
    )
  }
}

export default SmartyMenJacket
