import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

export class MainPage extends Component {
    constructor(props){
        super(props);

        this.state = { data: [], appName:''}               
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
    }

    async categoryChange(event) {
      const arr = event.target.value.split(',');   
      this.props.setInfoTrigger(false)
      this.props.setDataTrigger(false)
      this.props.setTriggerApp('')
      this.props.setAppInfo({
        server: arr[0],
        bundleId: arr[1],
        storeName: arr[2],
        appName: arr[3]
      })
      this.setState({appName: arr[3]}) 
    }
    
    activeStatus(event){
      console.log(this.state.appName);
      this.props.setInfoTrigger(true)
      this.props.setTriggerApp(this.state.appName)
      
      
    }

    MakeItem = function(X) {
      return <option value={X.value} key={X.label}>{X.label}</option>;
    };
    

  render() {
    return (
        <>
      <h1>APPS</h1>
      <div className='form-box'>
        <select onChange={this.categoryChange}>
          <option>Select Option</option>
          
          {this.props.categoryOptions.map(this.MakeItem)}
        </select>
        <input type="submit" value="Display Info" onClick={this.activeStatus}/>
      </div>

      </>
    )
  }
}

export default MainPage
