import React, { Component } from 'react'
import Select from 'react-select'
import 'bootstrap/dist/css/bootstrap.min.css';
import DisplayFrame from './DisplayFrame';

export class DisplayPanel extends Component {
    constructor(props){
        super(props);

        this.bundlesdata = {bundles: [], trigger: false};
        this.state = {active: 'NotActive', data: [] ,category: '', bundle: ''}
        
        this.handleSubmit = this.handleSubmit.bind(this);
        this.categorySelect = this.categorySelect.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.bundleSelect = this.bundleSelect.bind(this);
    }

    categorySelect(event){
      console.log('CATEGORY SUBMISSION',event.value);
      this.setState({category: event.value})

    }
    bundleSelect(event){
      console.log('BUNDLE SUBMISSION',event.value);
      this.setState({bundle: event.value})
    }

    activeStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'NotActive'){
        this.setState({active: 'Active'},()=>{
          console.log('Active status IF', this.state.active);

        })
      }
      
    }

    async handleSubmit(event){
      event.preventDefault();
      await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${this.state.bundle}`)
        .then((response) => response.json())
        .then( (actualData) => {
          console.log('response: ', actualData);
          if(actualData.status)
            this.setState({data: actualData.frames})
          else
            console.log("ERROR");
        })
        .catch((err) => {
         console.log(err.message);
        });
    }

  render() {
    const {trigger, setTrigger, pic, setPic} = this.props
    return (
        <>
      <h2>DisplayPanel</h2>
      <form className='form-box' onSubmit={this.handleSubmit}>
        <Select placeholder='Select Frame...' name='category' required id='category' className='' onChange={this.categorySelect} options={this.props.categoryOptions}/>
        <br/>
        <Select placeholder='Select Bundle...' name='bundle' id='bundle' className='' onChange={this.bundleSelect} options={this.props.bundleOptions}/>
        <br/>
        <input type="submit" value="SHOW" onClick={this.activeStatus}/>
      </form>

      <br/>
      {
        this.state.active === "Active" &&
        this.state.data.map(result=>(
          <>
            <img className='imgShow' key={result.image} src={result.image} alt="Frames" onClick={()=>{setTrigger(true); setPic(result.image)}}></img>
          </>
        ))
      }

      <DisplayFrame trigger={trigger} setTrigger={setTrigger} pic={pic} setPic={setPic}/>
      </>
    )
  }
}

export default DisplayPanel
