import { toHaveDisplayValue } from '@testing-library/jest-dom/dist/matchers';
import React, { Component } from 'react'
import Select from 'react-select'
import 'bootstrap/dist/css/bootstrap.min.css';

export class DisplayPanel extends Component {
    constructor(props){
        super(props);

        this.state = {active: 'NotActive', data: [] ,category: '', bundle: ''}
        this.categoryOptions = [
          {label: 'Love Frames', value: '5ff5a1d93bbf9374326985de'},
          {label: 'Comic Frames', value: '5ff7fab5f0dfd4c8479c5912'},
          {label: 'Birthday Frames', value: '5ff5a1d93bbf9374326985db'},
          {label: 'Magazine Frames', value: '5ff7fab5f0dfd4c8479c5913'},
          {label: 'News Frames', value: '5ff7fab5f0dfd4c8479c5914'},          
        ]
        this.bundleOptions = [
          {label: 'Bundle 1', value: 'bundle1'},
          {label: 'Bundle 2', value: 'bundle2'},
          {label: 'Bundle 3', value: 'bundle3'},
          {label: 'Bundle 4', value: 'bundle4'},
          {label: 'Bundle 5', value: 'bundle5'},
          {label: 'Bundle 6', value: 'bundle6'},
          {label: 'Bundle 7', value: 'bundle7'},
          {label: 'Bundle 8', value: 'bundle8'},
          {label: 'Bundle 9', value: 'bundle9'},
          {label: 'Bundle 10', value: 'bundle10'},
          {label: 'Bundle 11', value: 'bundle11'},
        ]
        this.handleSubmit = this.handleSubmit.bind(this);
        this.categorySelect = this.categorySelect.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.bundleSelect = this.bundleSelect.bind(this);
    }

    categorySelect(event){
      console.log('CATEGORY SUBMISSION',event.value);
      this.setState({category: event.value})

    }
    bundleSelect(event){
      console.log('BUNDLE SUBMISSION',event.value);
      this.setState({bundle: event.value})
    }

    activeStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'Active'){
        this.setState({active: 'NotActive'},()=>{
          console.log('Active status IF', this.state.active);

        })
      }
      else{
        this.setState({active: 'Active'}, ()=>{
          console.log('Active status ELSE', this.state.active);

        })
      }
    }

    async handleSubmit(event){
      event.preventDefault();
      await fetch(`http://172.16.0.107:9000/api/assets/temp?categoryId=${this.state.category}&bundleId=${this.state.bundle}`)
        .then((response) => response.json())
        .then( (actualData) => {
          this.setState({data: actualData.data})
        })
        .catch((err) => {
         console.log(err.message);
        });
    }

  render() {
    return (
        <>
      <div>DisplayPanel</div>
      <form onSubmit={this.handleSubmit}>
        <Select name='category' required id='category' className='col-2' onChange={this.categorySelect} options={this.categoryOptions}/>
        <Select name='bundle' id='bundle' className='col-2' onChange={this.bundleSelect} options={this.bundleOptions}/>
        <input type="submit" value="SUBMIT" onClick={this.activeStatus}/>
      </form>

      <br/>
      {
        this.state.active === "Active" &&
        this.state.data.map(result=>(
          <>
            <img key={result} src={result} alt="Frames"></img>
          </>
        ))
      }
      </>
    )
  }
}

export default DisplayPanel
