import './App.css';
import React, { useEffect, useState } from 'react';
import NameForm from './component/NameForm';
import DisplayPanel from './component/DisplayPanel';
import 'bootstrap/dist/css/bootstrap.min.css';


const [data, setData] = useState([])
const [active, setActive] = useState("NotActive")

function App() {

  const fetchdata= async (object) =>{

    console.log('values from FORM', object);
    await fetch(`http://172.16.0.107:9000/api/assets/temp?categoryId=${object.category}&bundleId=${object.bundle}`)
        .then((response) => response.json())
        .then(async (actualData) => {
          await setData(actualData.data);
        })
        .catch((err) => {
         console.log(err.message);
        });
  }

  useEffect(()=>{
    // fetchdata();
    console.log('in use effect',data);
  },[data])

  return (
    <div className="App">
      <NameForm/>
      <hr/>
      <DisplayPanel data={data} fetchdata={fetchdata} active={active} setActive={setActive}/>

      {
        data.map(images=>{
          <img src={images}></img>
        })
      }
    </div>
  );
}

export default App;
