import React, { Component } from 'react'

export class NameForm extends Component {
    constructor(props) {
        super(props);
        this.state = {H: 0, W: 0, X: 0, Y: 0, category: 'love', image: null};
        this.handleChangeX = this.handleChangeX.bind(this);
        this.handleChangeY = this.handleChangeY.bind(this);
        this.handleChangeH = this.handleChangeH.bind(this);
        this.handleChangeW = this.handleChangeW.bind(this);
        this.optionChange = this.optionChange.bind(this);
        this.onImageChange = this.onImageChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChangeX(event) {    this.setState({value: event.target.value});  }
      handleChangeY(event) {    this.setState({value2: event.target.value});  }
      handleChangeH(event) {    this.setState({value2: event.target.value});  }
      handleChangeW(event) {    this.setState({value2: event.target.value});  }
      optionChange(event) {    this.setState({option: event.target.value});  }
      onImageChange(event) {
        console.log(event.target.files);
        this.setState({image: event.target.files[0]});  
      }
      async handleSubmit(event) {
        
        const formData = new FormData();
        for (let x in this.state) {
          formData.append(x, this.state[x]);          //saving data in formData
       }
       var api;
       if(this.state.option == 'option 1'){
         api = "http://172.16.0.107:9001/api/rooms/checkImage1";
         console.log("API 1 CALLED");
        }else if(this.state.option == 'option 2'){
          api = "http://172.16.0.107:9001/api/rooms/checkImage2"
          console.log("API 2 CALLED");
       }

       const data = await fetch(api, {
           method: "post",
           // headers: { "Content-Type": "multipart/form-data" },
           body: formData,
         });
         const uploadedImage = await data.json();

        event.preventDefault();
      }
    
      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            
              Name:
              <label>Catergory: </label>
              <select name="selectList" id="selectList" onChange={this.optionChange}>
                 <option value="option 1">Option 1</option>
                 <option value="option 2">Option 2</option>
              </select>
              <br/>
              <input type="text" required value={this.state.X} onChange={this.handleChangeX} />
              <input type="text" required value={this.state.Y} onChange={this.handleChangeY} />
              <br/>
              <input type="text" required value={this.state.H} onChange={this.handleChangeH} />
              <input type="text" required value={this.state.W} onChange={this.handleChangeW} />
              <br/>
              <input type="file" required onChange={this.onImageChange} />
            <input type="submit" value="Submit" />
          </form>
        );
      }
}

export default NameForm
