  const [trigger, setTrigger]=useState(false)
  const [triggerDisplay, setTriggerDisplay]=useState(true)
  const [triggerAdd, setTriggerAdd]=useState(false)
  const [pic, setPic]=useState("")
  const categoryOptions = [
    {label: 'Love Frames', value: '5ff5a1d93bbf9374326985de', value2:'love'},
    {label: 'Comic Frames', value: '5ff7fab5f0dfd4c8479c5912', value2:'comicsframes'},
    {label: 'Birthday Frames', value: '5ff5a1d93bbf9374326985db', value2:'birthdayframes'},
    {label: 'Magazine Frames', value: '5ff7fab5f0dfd4c8479c5913', value2:'magzineframes'},
    {label: 'News Frames', value: '5ff7fab5f0dfd4c8479c5914', value2:'newsframes'},
  ];


  useEffect(()=>{
  },[])

  return (
    <div className="App">
      <Header setTriggerDisplay={setTriggerDisplay} setTriggerAdd={setTriggerAdd}/>

      {
        triggerDisplay === true &&
        <DisplayPanel 
          trigger={trigger} 
          setTrigger={setTrigger} 
          pic={pic} 
          setPic={setPic} 
          categoryOptions={categoryOptions}
        />
      }
      {
        triggerAdd === true &&
        <NameForm
          categoryOptions={categoryOptions}

        />

      }


    </div>
  );
}

export default App;
