import React, { Component } from 'react'

export class NameForm extends Component {
    constructor(props) {
        super(props);
        this.state = {value: '', value2: '', option: 'option 1', image: null};
        this.handleChange = this.handleChange.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.optionChange = this.optionChange.bind(this);
        this.onImageChange = this.onImageChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange(event) {    this.setState({value: event.target.value});  }
      handleChange2(event) {    this.setState({value2: event.target.value});  }
      optionChange(event) {    this.setState({option: event.target.value});  }
      onImageChange(event) {
        console.log(event.target.files);
        this.setState({image: event.target.files[0]});  
      }
      async handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value + this.state.value2 + this.state.option);
        const formData = new FormData();
        for (let x in this.state) {
          formData.append(x, this.state[x]);          //saving data in formData
       }
       var api;
       if(this.state.option == 'option 1'){
         api = "http://172.16.0.107:9001/api/rooms/checkImage1";
       }else if(this.state.option == 'option 2'){
        api = "http://172.16.0.107:9001/api/rooms/checkImage2"
       }

       const data = await fetch("http://172.16.0.107:9001/api/rooms/checkImage", {
           method: "post",
           // headers: { "Content-Type": "multipart/form-data" },
           body: formData,
         });
         const uploadedImage = await data.json();

        event.preventDefault();
      }
    
      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            
              Name:
              <input type="text" required value={this.state.value} onChange={this.handleChange} />
              <input type="text" required value={this.state.value2} onChange={this.handleChange2} />
              <select name="selectList" id="selectList" onChange={this.optionChange}>
                 <option value="option 1">Option 1</option>
                 <option value="option 2">Option 2</option>
              </select>
              <input type="file" required onChange={this.onImageChange} />
            <input type="submit" value="Submit" />
          </form>
        );
      }
}

export default NameForm
