import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


export class UploadNaturePhotos extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "Desert",value: "Desert"      },
          {label: "Forests",value: "Forests"    },
          {label: "Grass",value: "Grass"        },
          {label: "Mountain",value: "Mountain"  },
          {label: "Ocean",value: "Ocean"        }
        ]
        
        this.state = {
          data: [] ,
          category: '',
          trigger: false,
          background:[],
          overlay:[],
          thumbnail:[]
        }
        
        this.categoryChange = this.categoryChange.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
        this.handleBackground = this.handleBackground.bind(this)
        this.handleOverlay = this.handleOverlay.bind(this)
        this.handleThumbnail = this.handleThumbnail.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async categoryChange(event) {
      this.setState({category: event.target.value ,trigger: true, active: 'NotActive'})
      console.log('TRIGGER',this.state);
    }
    
    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

    handleBackground=(event)=>{
      // console.log('Submitting background', event.target.files);
      this.setState({background: event.target.files[0]});   
    }
    handleOverlay = (event)=>{
      // console.log('Submitting overlay', event.target.files);
      this.setState({overlay: event.target.files[0]});   
    }
    handleThumbnail = (event)=>{
      // console.log('Submitting thumbnail', event.target.files);
      this.setState({thumbnail: event.target.files[0]});   
    }
    handleSubmit = async (event)=>{
      event.preventDefault();
      const formData = new FormData();
      for (let x in this.state) {
        formData.append(x, this.state[x]);          //saving data in formData
        console.log(x, ': ', this.state[x]);
        
      }
      // console.log('-------------->>>>>>', this.formData);
       var api= "http://172.16.0.107:9001/api/rooms/sendImage";
       
      //  wait(1)
       if(api){
        const data = await fetch(api, {
          method: "post",
          // headers: { "Content-Type": "multipart/form-data" },
          body: formData,
        });
        const uploadedImage = await data.json();
        console.log(uploadedImage);
        alert("Frame is saved");
       }
    }

  render() {
    return (
        <>
            <h2>Nature Photos App</h2>
            <form className='form-box' onSubmit={this.handleSubmit}>
                <select onChange={this.categoryChange}>
                  <option>Select Option</option>

                  {this.categories.map(this.MakeItem)}
                </select>        
                
                {
                    this.state.trigger === true &&

                    <>
                        <br/>
                        <br/>
                        <b>Upload Background : </b>
                        <input type="file" required onChange={this.handleBackground}/>
                        <br/>
                        <b>Upload Overlay : </b>
                        &nbsp;&nbsp;&nbsp;
                        &nbsp;&nbsp;&nbsp;
                        <input type="file" required onChange={this.handleOverlay}/>
                        <br/>
                        <b>Upload Thumbnail : </b>
                        &nbsp;&nbsp;
                        <input type="file" required onChange={this.handleThumbnail}/>
                        <br/>
                    </>
                }
                <br/>    
                <input type="submit" value="Upload Data" onClick={()=>{}}/>

            </form>  
        </>
    )
  }
}

export default UploadNaturePhotos
