import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

import UploadPicCollageData from './UploadPicCollageData';

export class PicCollageMaker extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: "Book_cover",value: "Book_cover"  },
          {label: "Family",value: "Family"          },
          {label: "Frame",value: "Frame"            },
          {label: "Kid",value: "Kid"                },
          {label: "Magazine",value: "Magazine"      }
          ];  
        this.data = [];   
        this.state = {active :'NotActive', show: 'notActive'}
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.MakeItem = this.MakeItem.bind(this)
    }

    async categoryChange(event) {
      this.setState({active:'NotActive'})
      this.data = []
      
      await fetch(`http://164.68.111.220:9001/api/tas/collageAssetsByTag?tag=${event.target.value}`)
      .then((response) => response.json())
      .then( (actualData) => {
        this.state.show = 'Active'
        console.log('response: ', actualData);        
        if(actualData)
          this.data=actualData
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }

    activeStatus(event){
      console.log(this.state);
      if(this.state.active === 'NotActive'){
        this.setState({active:'Active'})
        console.log(this.state);
      }      
    }

    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

  render() {
    const {setTrigger, setPic} = this.props
    return (
        <>
      <h1>Pic Collage Maker</h1>
      <div className='form-box'>
        <select onChange={this.categoryChange}>
          <option>Select Option</option>
          
          {this.categories.map(this.MakeItem)}
        </select>
        <input type="submit" value="SHOW" onClick={this.activeStatus}/>
        <br/>
      </div>
        <input type="submit" value="Upload Data" onClick={()=>{this.props.setDataTrigger(true)}}/>

      <UploadPicCollageData
          dataTrigger={this.props.dataTrigger}
          setDataTrigger={this.props.setDataTrigger}
          MakeItem = {this.MakeItem}
          categories = {this.categories}
      />
      <br/>       
      {
        (this.state.active === "Active"&& this.data.length===0) &&
        
        <h1>Data Not Loaded Yet, Try Again</h1>
      }
      {
        (this.state.active === "Active" && this.state.show === 'Active') &&
        this.data.map(result=>(
          <>
            <img className='imgShow' key={result.image} src={result.image} alt="Frames" onClick={()=>{setTrigger(true); setPic(result.image)}}></img>
          </>
        ))
      }
      </>
    )
  }
}

export default PicCollageMaker
