import React, { Component } from 'react'
import Select from 'react-select'

export class AddBundle extends Component {
    constructor(props) {
        super(props);
        this.state = {bundle: '', category: 'love', images: []};
        this.handleChangeBundle = this.handleChangeBundle.bind(this);
        this.categoryChange = this.categoryChange.bind(this);
        this.onImagesChange = this.onImagesChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChangeBundle(event) {    this.setState({bundle: event.target.value}); console.log("bundle: ", event.target.value); }
      categoryChange(event) {    
        console.log('CATEGORY SUBMISSION',event.value);
        this.setState({category: event.value});  
      }
      onImagesChange(event) {
        console.log('multiple',typeof(event.target.files));
        console.log('multiple',event.target.files[0]);
        // var filesArr = [];
        // for(let a=0; a<event.target.files.length; a++){
        //   filesArr.push(event.target.files[a])
        // }
        // console.log("FILES",filesArr);
        for(var image of event.target.files)
          console.log("FILEFILEFILE", image);
        this.state.images.push(event.target.files);  
        console.log(this.state);
      }
      async handleSubmit(event) {
        
        event.preventDefault();
        const formData = new FormData();
        for (let x in this.state) {
          formData.append(x, this.state[x]);          //saving data in formData
          console.log(x, ': ', this.state[x]);
          
       }
       var api;
       if(this.state.category === 'love'){
         api = "http://172.16.0.107:9001/api/rooms/love";
         console.log("API 1 CALLED");
        }
        else if(this.state.category === 'comic'){
          api = "http://172.16.0.107:9001/api/rooms/comic"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'birthday'){
          api = "http://172.16.0.107:9001/api/rooms/birthday"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'magazine'){
          api = "http://172.16.0.107:9001/api/rooms/magazine"
          console.log("API 2 CALLED");
       }
        else if(this.state.category === 'news'){
          api = "http://172.16.0.107:9001/api/rooms/news"
          console.log("API 2 CALLED");
       }
       else{
         console.log("-----------------");
       }

      //  wait(1)
       if(api){
        const data = await fetch(api, {
          method: "post",
          // headers: { "Content-Type": "multipart/form-data" },
          body: formData,
        });
        const uploadedImage = await data.json();
        console.log(uploadedImage);
        alert("Frame is saved");
       }


      }

  render() {
    return (
      <>
        <h2>Add Bundle</h2>
          <form className='form-box' onSubmit={this.handleSubmit}>
            <Select placeholder='Select Category...' name='selectList' required id='selectList' onChange={this.categoryChange} options={this.props.categoryOptions}/>
            <br/>
                <br/>
                Bundle Name:&nbsp; 
                <input type="text" value={this.state.X} onChange={this.handleChangeBundle} />
                <br/>
                <br/>
                <br/>

                {/* <input type="file" required onChange={this.onImageChange} /> */}
                 <input type="file" multiple accept='image/*' onChange={this.onImagesChange} />
                <br/>
              <input type="submit" value="Submit" />
          </form>
      </>
    )
  }
}

export default AddBundle
