import React, { Component } from 'react'

export class NameForm extends Component {
    constructor(props) {
        super(props);
        this.state = {value: '', value2: '', option: '', image: null};
        this.handleChange = this.handleChange.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.optionChange = this.optionChange.bind(this);
        this.onImageChange = this.onImageChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange= input=> event=> {    this.setState({[input]: event.target.value});  }
      handleChange2(event) {    this.setState({value2: event.target.value});  }
      optionChange(event) {    this.setState({option: event.target.value});  }
      onImageChange(event) {
        console.log(event.target.files);
        this.setState({image: event.target.files[0]});  
      }
      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value + this.state.value2 + this.state.option);
        console.log(this.state.image);
        event.preventDefault();
      }
    
      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            
              Name:
              <input type="text" value={this.state.value} onChange={this.handleChange} />
              <input type="text" value={this.state.value2} onChange={this.handleChange2} />
              <select name="selectList" id="selectList" onChange={this.optionChange}>
                 <option value="option 1">Option 1</option>
                 <option value="option 2">Option 2</option>
              </select>
              <input type="file" onChange={this.onImageChange} />
            <input type="submit" value="Submit" />
          </form>
        );
      }
}

export default NameForm
