import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


export class LoveFramesApp extends Component {
    constructor(props){
        super(props);

        this.categories = [
          {label: 'Love Frames', value: '5ff5a1d93bbf9374326985de,love'},
          {label: 'Comic Frames', value: '5ff7fab5f0dfd4c8479c5912,comicsframes'},
          {label: 'Birthday Frames', value: '5ff5a1d93bbf9374326985db,birthdayframes'},
          {label: 'Magazine Frames', value: '5ff7fab5f0dfd4c8479c5913,magzineframes'},
          {label: 'News Frames', value: '5ff7fab5f0dfd4c8479c5914,newsframes'}
        ]
        
        this.state = {
          active: 'NotActive',
          data: [] ,
          category: '',
          bundle: '',
          bundles: [],
          trigger: false,
          bActive:true,
          X: '',
          Y: '',
          H:'',
          W:'',
          image:[]
        }
        
        this.categoryChange = this.categoryChange.bind(this);
        this.activeStatus = this.activeStatus.bind(this);
        this.inactiveStatus = this.inactiveStatus.bind(this);
        this.bundleChange = this.bundleChange.bind(this);
        this.MakeItem = this.MakeItem.bind(this);
        this.closeWindow = this.closeWindow.bind(this);
        this.handleX = this.handleX.bind(this)
        this.handleY = this.handleY.bind(this)
        this.handleH = this.handleH.bind(this)
        this.handleW = this.handleW.bind(this)
        this.handleImage = this.handleImage.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async categoryChange(event) {
        
      const arr = event.target.value.split(',')
      console.log('changing', event.target.value);
      this.setState({category: arr[0], active: 'NotActive', trigger: false}); 
      
      
      await fetch(`http://161.97.164.28:8080/api/bundles/retrieveBundles?category=${arr[0]}`)
      .then((response) => response.json())
      .then( (actualData) => {
        if(actualData.status)
        {
          this.setState({data: actualData.frames, bundles: actualData.data, trigger: true})
        }
        else
          console.log("ERROR");
      })
      .catch((err) => {
       console.log(err.message);
      }); 
    }
    async bundleChange(event){
      const bName = event.target.value.toLowerCase()
      this.setState({bundle: bName, active: 'NotActive'})

      await fetch(`http://161.97.164.28:8080/api/frames/getRequiredFrames?categoryId=${this.state.category}&bundleName=${bName}`)
        .then((response) => response.json())
        .then( (actualData) => {
          console.log('response: ', actualData);
          if(actualData.status){
            this.setState({data: actualData.frames})
            console.log('response:', this.state.data);
          }
          else
            console.log("ERROR");
        })
        .catch((err) => {
         console.log(err.message);
        });

    }
    
    MakeItem = function(X) {
      return <option value={X.value} key={X.value}>{X.label}</option>;
    };

    closeWindow=()=>{
      this.props.setDataTrigger(false);
      this.setState({trigger: false})
    }

    inactiveStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'Active'){
        this.setState({active: 'NotActive'},()=>{
          console.log('Not Active status IF', this.state.active);

        })
      }      
    }
    activeStatus(event){
      console.log(this.state.active);
      if(this.state.active === 'NotActive'){
        this.setState({active: 'Active'},()=>{
          console.log('Active status IF', this.state.active);

        })
      }      
    }

    handleX=(event)=>{console.log(event.target.value);}
    handleY=(event)=>{console.log(event.value);}
    handleH=(event)=>{console.log(event.value);}
    handleW=(event)=>{console.log(event.value);}
    handleImage=(event)=>{console.log(event.target.files);}
    handleSubmit = (event)=>{
      console.log('Submitting Data', event);
      alert('Data Submitted')
    }

  render() {
    const {setTrigger, setPic} = this.props
    return (
        <>
            <h2>Love Frames App</h2>
            <form className='form-box'>
                <select onChange={this.categoryChange}>
                  <option>Select Option</option>

                  {this.categories.map(this.MakeItem)}
                </select>        
                {
                  this.state.trigger === true &&
                  <>
                    <br/>
                      <select onChange={this.bundleChange}>
                        <option>Select Option</option>
                    
                        {this.state.bundles.map(this.MakeItem)}
                      </select>          
                  </>
                }
                {
                    this.state.trigger === true &&

                    <>
                        <br/>
                        <br/>
                        Coordinates
                        <br/>
                        X:&nbsp;
                        <input type="text1" onChange={()=>{}}/>
                        &nbsp;&nbsp;&nbsp;
                        Y:&nbsp;
                        <input type="text2" value='' onChange={()=>{}}/>
                        <br/>
                        <br/>
                        Position
                        <br/>
                        H:&nbsp;
                        <input type="text3" value='' onChange={()=>{}}/>
                        &nbsp;&nbsp;
                        W:&nbsp;
                        <input type="text4" value='' onChange={()=>{}}/>
                        <br/>
                        <br/>
                        <b>Upload Frame: </b>
                        <input type="file" required />
                    </>
                }

                <br/>    
                <input type="submit" value="Upload Data" onClick={()=>{}}/>
            </form>  
        </>
    )
  }
}

export default LoveFramesApp
